import React from "react";

export const Header: React.FC = () => {
  return (
    <header className="py-6 px-10 w-full bg-zinc-300 shadow-md">
      <div className="flex justify-between items-center max-w-7xl mx-auto">
        {/* Logo and Title */}
        <div className="flex items-center gap-4">
          <img
            src="https://api.builder.io/api/v1/image/assets/TEMP/54bc316094eb330e0bda616cecb3e12b733bac38?placeholderIfAbsent=true&apiKey=b59f74ae8c9444f28ee33eff06842d33"
            alt="The Recipe Room Logo"
            className="w-12 h-12 object-contain rotate-[-15deg] transition-transform duration-300 ease-in-out"
          />
          <h1 className="text-3xl font-bold text-black">The Recipe Room</h1>
        </div>

        {/* Navigation */}
        <nav className="flex space-x-12 text-xl font-semibold text-black">
          {["Home", "Recipe Idea", "Add Recipe", "About Us"].map((text, idx) => (
            <a
              key={idx}
              href="#"
              className="relative group transition duration-300 ease-in-out hover:text-gray-700"
            >
              {text}
              <span className="absolute left-0 bottom-[-4px] w-0 h-[2px] bg-black transition-all group-hover:w-full duration-300"></span>
            </a>
          ))}
        </nav>
      </div>
    </header>
  );
};
