import React, { useRef } from "react";
import { RecipeCard, RecipeCardProps } from "./RecipeCard";

interface RecipeSliderProps {
  items: RecipeCardProps[];
}

export const RecipeSlider: React.FC<RecipeSliderProps> = ({ items }) => {
  const sliderRef = useRef<HTMLDivElement>(null);

  const scrollLeft = () => {
    sliderRef.current?.scrollBy({ left: -300, behavior: "smooth" });
  };

  const scrollRight = () => {
    sliderRef.current?.scrollBy({ left: 300, behavior: "smooth" });
  };

  return (
    <div className="relative w-full">
      {/* Left Arrow */}
      <button
        onClick={scrollLeft}
        className="absolute left-0 top-1/2 -translate-y-1/2 z-10 bg-white p-2 rounded-full shadow hover:bg-red-500 hover:text-white transition"
        aria-label="Scroll Left"
      >
        &#8592;
      </button>

      {/* Scroll Container */}
      <div
        ref={sliderRef}
        className="flex overflow-x-auto scroll-smooth snap-x snap-mandatory gap-5 px-10 scrollbar-hide"
      >
        {items.map((item, idx) => (
          <div key={idx} className="snap-start flex-shrink-0 w-64">
            <RecipeCard {...item} />
          </div>
        ))}
      </div>

      {/* Right Arrow */}
      <button
        onClick={scrollRight}
        className="absolute right-0 top-1/2 -translate-y-1/2 z-10 bg-white p-2 rounded-full shadow hover:bg-red-500 hover:text-white transition"
        aria-label="Scroll Right"
      >
        &#8594;
      </button>
    </div>
  );
};
