import React from "react";
import { useParams } from "react-router-dom";
import { allRecipes } from "../data/allRecipes";

export const RecipeDetail: React.FC = () => {
  const { id } = useParams<{ id: string }>();
  const recipe = allRecipes.find((item) => item.id === id);

  if (!recipe) {
    return <div className="p-6 text-center text-red-600 text-xl">Recipe not found</div>;
  }

  return (
    <div className="p-6 max-w-6xl mx-auto bg-white shadow-md rounded-lg mt-10 flex flex-col md:flex-row gap-6">
      <div className="md:w-1/2">
        <img
          src={recipe.image}
          alt={recipe.title}
          className="w-[1000px] h-[500px] object-cover rounded-md"
        />
      </div>

      <div className="md:w-1/2">
        <h1 className="text-3xl font-bold mb-2">{recipe.title}</h1>
        <p className="mb-4 text-gray-700">{recipe.description}</p>

        <div className="mb-2">
          <span className="font-semibold">Price:</span> {recipe.price}
        </div>
        <div className="mb-4">
          <span className="font-semibold">Quantity:</span> {recipe.quantity}
        </div>

        <div className="mb-4">
          <h2 className="text-2xl font-semibold mb-2">Ingredients</h2>
          <ul className="list-disc list-inside">
            {recipe.ingredients?.map((ingredient, index) => (
              <li key={index}>{ingredient}</li>
            ))}
          </ul>
        </div>

        <div>
          <h2 className="text-2xl font-semibold mb-2">Instructions</h2>
          <ol className="list-decimal list-inside">
            {recipe.instructions?.map((step, index) => (
              <li key={index}>{step}</li>
            ))}
          </ol>
        </div>
      </div>
    </div>
  );
};
