import React from "react";
import { useParams } from "react-router-dom";
import { allRecipes } from "../data/allRecipes";

export const RecipeDetail: React.FC = () => {
  const { id } = useParams<{ id: string }>();
  const recipe = allRecipes.find((item) => item.id === id);

  if (!recipe) return <div className="p-4">Recipe not found</div>;

  return (
    <div className="p-8 bg-white">
      <h1 className="text-3xl font-bold mb-4">{recipe.title}</h1>
      <img src={recipe.image} alt={recipe.title} className="w-full max-w-md mb-4" />
      <p className="text-lg mb-2">{recipe.description}</p>
      <p><strong>Price:</strong> {recipe.price}</p>
      <p><strong>Quantity:</strong> {recipe.quantity}</p>
      <p><strong>Rating:</strong> {recipe.rating} ‚≠ê</p>

      <h2 className="mt-6 font-bold text-xl">Ingredients</h2>
      <ul className="list-disc pl-6">
        {recipe.ingredients?.map((item: string, i: number) => (
          <li key={i}>{item}</li>
        ))}
      </ul>

      <h2 className="mt-6 font-bold text-xl">Instructions</h2>
      <ol className="list-decimal pl-6">
        {recipe.instructions?.map((step: string, i: number) => (
          <li key={i}>{step}</li>
        ))}
      </ol>
    </div>
  );
};
