import React, { useEffect } from "react";
import { useNavigate } from "react-router-dom";

const WelcomePage: React.FC = () => {
  const navigate = useNavigate();

  useEffect(() => {
    // Clear visited flag every time welcome page loads
    localStorage.removeItem("hasVisited");

    const timer = setTimeout(() => {
      localStorage.setItem("hasVisited", "true");
      navigate("/home");
    }, 3000); // 3 seconds delay

    return () => clearTimeout(timer);
  }, [navigate]);

  return (
    <div className="h-screen bg-gradient-to-br from-yellow-100 to-red-200 flex flex-col items-center justify-center text-center px-4">
      <img
        src="https://api.builder.io/api/v1/image/assets/TEMP/54bc316094eb330e0bda616cecb3e12b733bac38?apiKey=b59f74ae8c9444f28ee33eff06842d33"
        alt="Logo"
        className="w-24 h-24 mb-6 animate-fade-in-up"
      />
      <h1 className="text-5xl font-bold mb-4 animate-fade-in-up">
        Welcome to The Recipe Room
      </h1>
      <p className="text-xl mb-8 animate-fade-in-up">
        Discover mouth-watering recipes from around the world.
      </p>
    </div>
  );
};

export default WelcomePage;
