import React from "react";

interface RecipeItem {
  image: string;
  imageAlt?: string;
  title: string;
  price: string;
  rating: number;
  quantity: string;
  description: string;
}

interface RecipeCardProps {
  items: RecipeItem[];
  renderStars: (rating: number) => React.ReactNode;
}

export const RecipeCard: React.FC<RecipeCardProps> = ({ items, renderStars }) => {
  return (
    <section className="w-full">
      <div className="mt-4 w-full max-w-[1596px] max-md:max-w-full overflow-x-auto scroll-smooth snap-x snap-mandatory flex gap-5 px-5">
        {items.map((item: RecipeItem, index: number) => (
          <div
            key={index}
            className="w-3/12 max-md:w-full snap-start flex-shrink-0"
          >
            <article
              className="flex flex-col h-[400px] w-full rounded-2xl bg-zinc-300 overflow-hidden max-md:mt-10 
                transition-all duration-300 ease-in-out 
                hover:shadow-[0_8px_24px_rgba(255,0,0,0.4)] hover:scale-[1.02] cursor-pointer"
            >
              <img
                src={item.image}
                alt={item.imageAlt || item.title}
                className="object-cover w-full h-48 rounded-t-2xl"
              />
              <div className="flex flex-col px-3 py-4 grow">
                <div className="flex justify-between items-start">
                  <div className="flex flex-col text-xl font-bold text-black">
                    <h3>{item.title}</h3>
                    <div className="mt-1.5">{item.price}</div>
                  </div>
                  <div className="flex flex-col items-end">
                    <div className="flex gap-px">{renderStars(item.rating)}</div>
                    <div className="mt-2 text-xl font-bold text-black">
                      {item.quantity}
                    </div>
                  </div>
                </div>
                <p className="mt-auto text-lg text-black truncate">
                  {item.description}
                </p>
              </div>
            </article>
          </div>
        ))}
      </div>
    </section>
  );
};
