import React from "react";
import { useParams } from "react-router-dom";
import { indianDishes } from "../data/indianDishes";
import { italianDishes } from "../data/italianDishes";
import { drinks } from "../data/drinks";

const allRecipes = [...indianDishes, ...italianDishes, ...drinks];

export const RecipeDetail: React.FC = () => {
  const { id } = useParams<{ id: string }>();
  const recipe = allRecipes.find((item) => item.id === id);

  if (!recipe) {
    return <div className="p-4 text-red-500 text-lg">Recipe not found</div>;
  }

  return (
    <div className="p-6 max-w-4xl mx-auto">
      <h1 className="text-3xl font-bold mb-4">{recipe.title}</h1>
      <img
        src={recipe.image}
        alt={recipe.title}
        className="w-full h-64 object-cover rounded-lg mb-6"
      />
      <div className="text-gray-700 mb-4">{recipe.description}</div>
      <div className="mb-4">
        <span className="font-semibold">Price:</span> {recipe.price}
      </div>
      <div className="mb-4">
        <span className="font-semibold">Quantity:</span> {recipe.quantity}
      </div>
      <div className="mb-4">
        <span className="font-semibold">Rating:</span> {recipe.rating} ‚≠ê
      </div>
      <div className="mb-4">
        <h2 className="text-xl font-semibold mb-2">Ingredients</h2>
        <ul className="list-disc list-inside">
          {recipe.ingredients?.map((item, idx) => (
            <li key={idx}>{item}</li>
          ))}
        </ul>
      </div>
      <div>
        <h2 className="text-xl font-semibold mb-2">Instructions</h2>
        <ol className="list-decimal list-inside">
          {recipe.instructions?.map((step, idx) => (
            <li key={idx}>{step}</li>
          ))}
        </ol>
      </div>
    </div>
  );
};
